              src="https://images.unsplash.com/photo-1665323759004-43c9f3b941dc?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxlZGl0b3JpYWwtZmVlZHwyfHx8ZW58MHx8fHw%3D&auto=format&fit=crop&w=500&q=60"
              alt=""
            />
          </div>
        </section>
      </main>
      </div>
    </>
  );
}

export default DarkMood;
